import React from 'react';
//import logo from './logo.svg';
import './App.css';

class SentMessages extends React.Component {
  constructor(props) {
      super(props);
      this.state = { messages: [] };
  }

  componentDidMount() {
    fetch('/')
      .then(res => {console.log(res.json()); return res.json()})
      .then(messages => this.setState({ messages }));
  }

