  render() {
    return (
      <dl>Sent Messages
        {this.state.messages.map((message) =>
          <dt key={message.id}>{message.body}
              <dd>{message.date}</dd>
              <dd>{message.no}</dd>
          </dt>
        )}
      </dl>
      )
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
      </header>
      <h1>Messages</h1>
      <SentMessages />
    </div>
  );
}

export default App;
