import React from 'react';
import logo from './logo.svg';
import './App.css';
import Headroom from "react-headroom";

class SentMessages extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        allMessages: [],
        loaded: false,
      }
  }

  componentDidMount() {
    fetch('/messages')
      .then(res => res.json())
      .then(loadedMessages => this.setState({ allMessages: loadedMessages, loaded: true }))
      .catch((err) => this.setState({ loaded: true }));
  }

  render() {
    if (this.state.allMessages.length === 0 && this.state.loaded === false) {
      return <p className="status">Loading messages...</p>
    } else if (this.state.allMessages.length === 0 && this.state.loaded === true) {
      return <p className="status">No messages to be found.</p>
    } else {
      return (
        <div className="messageFeed">
          {this.state.allMessages.map((message) =>
            <div key={message.id} className="message">
              <p className="body">{message.body}</p>
              <p className="date">{message.date}</p>
              <p className="rec">{message.rec}</p>
            </div>
          )}
        </div>
      )
    }
  }
}

function App() {
  return (
    <div className="App">
      <Headroom>
        Here is the top of the world
      </Headroom>
      <h2>Messages</h2>
      <SentMessages />
    </div>
  );
}

export default App;
