const api = {
  baseUrl: 'https://api.twilio.com'
};

class SentMessages extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        allMessages: [],
        nextPage: null,
        hasMoreItems: true
      }
  }

  componentDidMount() {
    fetch('/messages')
      .then(res => res.json())
      .then(res => this.setState({
              allMessages: res.messages,
              nextPage: res.next_page_uri
            }))
      .catch((err) => this.setState({ loaded: true }));
  }

