  }
  render() {
    if (this.state.allMessages.length === 0 && this.state.loaded === false) {
      return <p className="status">Loading messages...</p>
    } else if (this.state.allMessages.length === 0 && this.state.loaded === true) {
      return <p className="status">No messages to be found.</p>
    } else {
      return (
        <div className="messageFeed">
          {this.state.allMessages.map((message) =>
            <div key={message.id} className="message">
              <p className="body">{message.body}</p>
              <p className="date">{message.date_sent}</p>
              <p className="rec">{message.to}</p>
              <p>{message.nextPage}</p>
            </div>
          )}
        </div>
      )
    }
  }
