      url = api.baseUrl + this.state.nextPage;
    }
    const obj = {
      method: 'POST',
      body: {
        url : url
      }
    };
    fetch(url, obj)
      .then(res => res.json())
      .then(res => {
        if (res) {
          var updatedMessages = self.state.allMessages;
          res.messages.map(message => {
            updatedMessages.push(message);
          })
          if (res.nextPage) {
            self.setState({
              allMessages: updatedMessages,
              nextPage: res.nextPage
            });
          }
        } else {
          self.setState({
