import logo from './logo.svg';
import './App.css';

class SentMessages extends React.Component {
  constructor(props) {
      super(props);
      this.state = { messages: [] };
  }

  componentDidMount() {
    fetch('/').then(console.log(res.json()));
      //.then(res => {console.log(res.json()); return res.json()})
      //.then(messages => this.setState({ messages }));
  }

  Messages(messageList) { //could change each of these to function (modular)
    const listItems = messageList.messages.map((message) =>
        <dt key={message.id}>{message.body}
            <dd>{message.date}</dd>
            <dd>{message.no}</dd>
        </dt>
        );
    return <dl>{listItems}</dl>;
}

  render() {
      return <this.Messages>messageList={this.state}</this.Messages>;
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
      </header>
      <h1>Messages</h1>
      {/*<SentMessages />*/}
    </div>
  );
}

export default App;
