import React from 'react';
//import logo from './logo.svg';
import './App.css';

class SentMessages extends React.Component {
  constructor(props) {
      super(props);
      this.state = {};
  }

  componentDidMount() {
    fetch('/')
      .then(res => res.json())
      .then(messages => this.setState({ messages }));
  }

  Messages(messageList) { //could change each of these to function (modular)
    const listItems = messageList.messages.map((message) =>
        <dt key={message.id}>{message.body}
            <dd>{message.date}</dd>
            <dd>{message.no}</dd>
        </dt>
        );
    return <dl>{listItems}</dl>;
}

  render() {
    while(this.state.messages.length == 0) {
      console.log(this.state);
    }
    console.log(this.state);
    return <this.Messages>messageList={this.state}</this.Messages>;
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
      </header>
      <h1>Messages</h1>
      <SentMessages />
    </div>
  );
}

export default App;
